import React, { Component } from 'react';

import { withFirebase } from '../Firebase';

import { Col,Media } from 'reactstrap';


class OrgsPage extends Component {
  constructor(props) {
    super(props);

    this.state = {
      loading: false,
      orgs: [],
    };
  }

  componentDidMount() {
    this.setState({ loading: true });

    this.props.firebase.orgs().on('value', snapshot => {
      const orgsObject = snapshot.val();

      const orgsList = Object.keys(orgsObject).map(key => ({
        ...orgsObject[key],
        uid: key,
      }));

      this.setState({
        orgs: orgsList,
        loading: false,
      });
    });
  }


  componentWillUnmount() {
    this.props.firebase.orgs().off();
  }

  render() {
    const { orgs, loading } = this.state;

    return (
      <div>
        <h1>Organizaciones</h1>

        {loading && <div>Loading ...</div>}

        <OrgSList orgs={orgs} />
      </div>
    );
  }
}

const OrgSList = ({ orgs }) => (
  <ul>
    {orgs.map(org => (
      <li key={org.nombre}>

    <Media>
      <Media left href={org.logo}>
        <Media object src={org.logo} alt="Generic placeholder image" data-width="300" data-height="300"/>
      </Media>
      <Media body>
        <Media heading>
          {org.nombre}
        </Media>
          Descripcion de la organizacion        
        </Media>
    </Media>


      </li>
    ))}
  </ul>
);

export default withFirebase(OrgsPage);
