import React, { Component } from 'react';

import { withFirebase } from '../Firebase';

import { Col,Media,Badge,Button,Alert } from 'reactstrap';
import { Link } from 'react-router-dom';

import { BrowserRouter as Router, Route } from 'react-router-dom';
import * as ROUTES from '../../constants/routes';

class EncuestasPage extends Component {
  constructor(props) {
    super(props);

    this.state = {
      loading: false,
      org: props.match.params.org,
      objeto:null,  
    };
  }

  componentDidMount() {
    this.setState({ loading: true });

    this.props.firebase.o_rg(this.state.org).on('value', snapshot => {

        const org_object = snapshot.val();
        this.setState({
            loading: false,
            org: this.state.org,
            objeto:org_object, 

        });
    });
  }




  componentWillUnmount() {
    this.props.firebase.orgs().off();
  }


  render() {
    const { org, loading,objeto } = this.state;

    return (

      <div>
        <h1>Encuestas de </h1>
        <h1>{org}</h1>
        <h1>{objeto.nombre}</h1>

        {loading && <div>Loading ...</div>}

      </div>
    );
  }
}




export default withFirebase(EncuestasPage);
