import React, { Component } from 'react';

import { withFirebase } from '../Firebase';

import { Col,Media,Badge,Button,Alert } from 'reactstrap';
import { Link } from 'react-router-dom';

import { BrowserRouter as Router, Route } from 'react-router-dom';
import * as ROUTES from '../../constants/routes';

class EncuestasPage extends Component {
  constructor(props) {
    super(props);

    this.state = {
      loading: false,
      org: props.match.params.org,
      objeto:[],  
    };
  }

  componentDidMount() {
    this.setState({ loading: true });

    this.props.firebase.o_rg(this.state.org).on('value', snapshot => {

        const org_object = snapshot.val();
        //console.log(org_object);
        this.setState({
            loading: false,
            org: this.state.org,
            objeto:org_object, 

        });
    });
  }




  componentWillUnmount() {
    this.props.firebase.orgs().off();
  }


  render() {
    const { org, loading,objeto } = this.state;

    return (

      <div>
        <h1>Encuestas de {objeto.nombre}</h1>



        {loading && <div>Loading ...</div>}

      </div>
    );
  }
}


const OrgSList = ({ encuestas }) => (
  <ul>
    {encuestas.map(org => (
      <li key={org.nombre}>

    <Media>
      <Media left >

        <Media object src={org.logo} style={imgStyle} alt="Generic placeholder image" />

      </Media>
      <Media body>
        <Media heading middle>
         &emsp;
         <Badge href={org.logo} color="primary">{org.nombre}</Badge>
        </Media>
          Descripcion de la organizacion        
         <Link to={"/organizations/encuestas/"+org.uid } >Ver informacion</Link>
        </Media>
    </Media>


      </li>
    ))}
  </ul>
);



export default withFirebase(EncuestasPage);
