import React, { Component } from 'react';

import { withFirebase } from '../Firebase';

class OrgsPage extends Component {
  constructor(props) {
    super(props);

    this.state = {
      loading: false,
      orgs: [],
    };
  }

  componentDidMount() {
    this.setState({ loading: true });

    this.props.firebase.orgs().on('value', snapshot => {
      const orgsObject = snapshot.val();

      const orgsList = Object.keys(orgsObject).map(key => ({
        ...orgsObject[key],
        uid: key,
      }));

      this.setState({
        orgs: orgsList,
        loading: false,
      });
    });
  }


  componentWillUnmount() {
    this.props.firebase.orgs().off();
  }

  render() {
    const { orgs, loading } = this.state;

    return (
      <div>
        <h1>Organizaciones</h1>

        {loading && <div>Loading ...</div>}

        <OrgSList orgs={orgs} />
      </div>
    );
  }
}

const OrgSList = ({ orgs }) => (
  <ul>
    {orgs.map(org => (
      <li key={org.uid}>
        <span>
          <strong>Nombre:</strong> {org.nombre}
        </span>

        <span>
           <Col xs={6} md={4}>
                <Image src="/thumbnail.png" rounded />
           </Col>
        </span>
      </li>
    ))}
  </ul>
);

export default withFirebase(OrgsPage);
